        <div class="container">
          <FormulateInput
            type="text"
            name="name"
            label="Введіть ваше ім`я"
            v-model="name"
          />
          <FormulateInput
            type="number"
            name="sample"
            label="Введіть ваш вік"
            min="0"
            max="100"
            error-behavior="live"
            v-model="age"
          />
          <FormulateInput
            type="number"
            name="sample"
            label="Введіть вашу вагу"
            min="0"
            max="400"
            error-behavior="live"
            v-model="weight"
          />
          <FormulateInput
            type="number"
            name="sample"
            label="Введіть ваш зріст"
            min="0"
            max="400"
            error-behavior="live"
            v-model="growth"
          />
          <button class="btn btn-success start-button mt-3">
