<script>
export default {
  data() {
    return {
      name: '',
      weight: '',
      growth: '',
      age: '',
    }
  },



  computed: {
    isSmallGrowth() {
      if (this.growth <= 178 && this.weight < 90  ) {
        return true;
      }
    },
    isHighGrowth() {
      if (this.growth > 178 && this.weight < 90 ) {
        return true;
      }
    },
    isBig() {
      if ( this.weight >= 90) {
        return true;
      }
    }

  },
  mounted() {
    if(localStorage.name) this.name = localStorage.name;
    if(localStorage.weight) this.weight = localStorage.weight;
    if(localStorage.growth) this.growth = localStorage.growth;
    if(localStorage.age) this.age = localStorage.age;
  },
  watch:{
    name(newName) {
      localStorage.name = newName;
    },
    weight(newWeight) {
      localStorage.weight = newWeight;
    },
    growth(newGrowth) {
      localStorage.growth = newGrowth;
    },
    age(newAge) {
      localStorage.age = newAge;
    }
  }
}

</script>

